import { Modal } from "react-bootstrap";
import { Button, TextField, Stack } from "@mui/material";
import { useState } from "react";

export default function MyVerticallyCenteredModal(props) {
  const { quotes, setQuotes } = props;
  const [quoteValue, setQuoteValue] = useState()
  const handleSubmit = (e) => {
    e.preventDefault();
    setQuotes([...quotes, quoteValue])
    console.log(quoteValue, quotes)
  };

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Write an Idea
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <TextField
          fullWidth
          focused
          id="inputQuote"
          type="text"
          placeholder='"Ill be back"'
          name="quoteInput"
          value={quoteValue}
          label="Quote"
          onChange={e => setQuoteValue(e.target.value)}
          multiline
          rows={4}
        />
      </Modal.Body>
      <Modal.Footer>
        <Stack spacing={1} direction="row">
          <Button variant="outlined" onClick={props.onHide}>Cancel</Button>
          <Button variant="contained" onClick={props.onHide} type="submit">Save</Button>
        </Stack>
      </Modal.Footer>
    </Modal>
  );
}
